import React from "react";
import Content from "./Content";
import Slider from "./slider";
import RadioBtn from "./RadioBtn";
import Dropdown from "./Dropdown";

import classes from "./Form.modules.css";
const FormAdvance = () => {
  return (
    <div className="container">
      <div className="filter">
        <form action="">
          <div className="control-group">
            <div>
              <label htmlFor="name">News paper name</label>
              <select
                name="newspaper"
                className="form-select col-md-3"
                required="True"
                value={PaperName}
                onChange={PaperChangeHandler}
                onBlur={PaperBlurhandler}
              >
                <option value="">Select Newspaper</option>
                {data.papers.map((paper) => (
                  <option value={paper.value}>{paper.value}</option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="name">City</label>
              <select
                name="city"
                className="form-select col-md-3"
                required="True"
                value={CityName}
                onChange={CityChangeHandler}
                onBlur={CityBlurhandler}
              >
                <option value="">Select City</option>
                {data.cities.map((city) => (
                  <option value={city.value}>{city.value}</option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor="name">Sub</label>
              <select
                name="sub"
                className="form-select col-md-3"
                required="True"
                value={subName}
                onChange={subChangeHandler}
                onBlur={SubBlurhandler}
              >
                <option value="">Select User</option>
                {data.UserName.map((sub) => (
                  <option value={sub.value}>{sub.value}</option>
                ))}
              </select>
            </div>
          </div>
        </form>
      </div>
      <div className="information-container">
        <Slider />
        <form>
          <div className="form-btn">
            <RadioBtn name="Positive" />
            <RadioBtn name="Negative" />
            <RadioBtn name="Positive/Negative" />
            <Dropdown selected="Select" />
            <button>Submit</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormAdvance;
